import React, { useState } from "react";
import { Button, Text, TextInput, View } from "react-native";
import { ScreenFC } from "../models/ScreenFC";
import { useDispatch } from "react-redux";
import { signUp } from "../redux/actions/accountActions";

const SignUp: ScreenFC<"SignUp"> = ({ navigation }) => {
  const [email, setEmail] = useState<string>();
  const [password, setPassword] = useState<string>();
  const [name, setName] = useState<string>();
  const [surname, setSurname] = useState<string>();
  const [birthdate, setBirthdate] = useState<Date>();
  const [nationality, setNationality] = useState<string>();
  const [city, setCity] = useState<string>();
  const [phone, setPhone] = useState<string>();
  const [profileImage, setProfileImage] = useState<string>();

  const dispatch = useDispatch();

  const handleSignUp = () => {
    if (
      email &&
      password &&
      name &&
      surname &&
      birthdate &&
      nationality &&
      city &&
      phone &&
      profileImage
    ) {
      dispatch(
        signUp({
          email,
          password,
          name,
          surname,
          birthdate,
          nationality,
          city,
          phone,
          profileImage,
          isLogged: true,
        })
      );
    }
  };

  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
      <Text>Registrati</Text>
      <TextInput
        placeholder="Nome"
        onChangeText={(value) => setName(value)}
      />
      <TextInput
        placeholder="Cognome"
        onChangeText={(value) => setSurname(value)}
      />
      <TextInput
        placeholder="Data di nascita"
        onChangeText={(value) => setBirthdate(new Date(value))}
      />
      <TextInput
        placeholder="Nazionalità"
        onChangeText={(value) => setNationality(value)}
      />
      <TextInput
        placeholder="Città"
        onChangeText={(value) => setCity(value)}
      />
      <TextInput
        placeholder="Telefono"
        onChangeText={(value) => setPhone(value)}
      />
      <TextInput
        placeholder="email"
        onChangeText={(value) => setEmail(value)}
      />
      <TextInput
        placeholder="password"
        onChangeText={(value) => setPassword(value)}
      />
      <TextInput
        placeholder="Carica immagine del profilo"
        onChangeText={(value) => setProfileImage(value)}
      />
      <Button title="Registrati" color="red" onPress={handleSignUp} />
      <View style={{ flexDirection: "row", alignItems: "center" }}>
        <Text>Hai un account?</Text>
        <Button title="Accedi" onPress={() => navigation.navigate("Login")} />
      </View>
    </View>
  );
};

export default SignUp;
