import React, { useState } from "react";
import { Button, Image, Text, TextInput, View } from "react-native";
import { ScreenFC } from "../models/ScreenFC";
import { useDispatch, useSelector } from "react-redux";
import { Account, AccountAction, ACCOUNT_ACTIONS, AccountProps, logout } from "../redux/actions/accountActions";
import * as ImagePicker from 'expo-image-picker';

const ProfileScreen: ScreenFC<"Profile"> = () => {
  const { account } = useSelector(
    (state: { accountReducer: AccountProps }) => state.accountReducer
  );

  const dispatch = useDispatch();

  const [email, setEmail] = useState<string>(account.email);
  const [password, setPassword] = useState<string>(account.password);
  const [imageUri, setImageUri] = useState<string>(account.image);
  const [firstName, setFirstName] = useState<string>(account.firstName);
  const [lastName, setLastName] = useState<string>(account.lastName);
  const [dateOfBirth, setDateOfBirth] = useState<string>(account.dateOfBirth);
  const [nationality, setNationality] = useState<string>(account.nationality);
  const [city, setCity] = useState<string>(account.city);
  const [phoneNumber, setPhoneNumber] = useState<string>(account.phoneNumber);
  const [isEditing, setIsEditing] = useState<boolean>(false);
const [isTextInputEditable, setIsTextInputEditable] = useState<boolean>(false);

  interface CustomImagePickerResult {
    cancelled: boolean;
    uri: string;
    type?: string;
    width?: number;
    height?: number;
  }

  const handleEdit = () => {
    setIsEditing(true);
    setIsTextInputEditable(true);
  };


  const handleSaveChanges = () => {
    setIsEditing(false);
    setIsTextInputEditable(false);
    if (isEditing) {
      dispatch({
        type: ACCOUNT_ACTIONS.UPDATE_ACCOUNT,
        payload: {
          email,
          password,
          isLogged: true,
          firstName,
          lastName,
          dateOfBirth,
          nationality,
          city,
          phoneNumber,
          image: imageUri,
        },
      } as AccountAction);
    } else {
      dispatch({
        type: ACCOUNT_ACTIONS.ADD_ACCOUNT,
        payload: {
          email,
          password,
          isLogged: true,
          firstName,
          lastName,
          dateOfBirth,
          nationality,
          city,
          phoneNumber,
          image: imageUri,
        },
      } as AccountAction);
    }
    setIsEditing(false);
  };

  const handleImagePicker = async () => {
    const { status } =
      await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (status !== "granted") {
      alert("Per favore, abilita i permessi per la libreria multimediale.");
      return;
    }
    const result = (await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      quality: 1,
    })) as unknown as CustomImagePickerResult;
    if (!result.cancelled) {
      setImageUri(result.uri);
    }
  };

  const handleTextInputChange = (setState: Function, newValue: string) => {
    if (isEditing) {
      setState(newValue);
    }
  };

  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
    <Image source={{ uri: imageUri }} style={{  maxWidth: 200,
      maxHeight: 200,
      width: '100%',
      height: '100%',
      borderRadius: 100, }}  resizeMode="cover"/>
    <Button title="Cambia immagine" onPress={handleImagePicker} />
    <Text>Nome</Text>
    <TextInput value={firstName} onChangeText={isEditing ? setFirstName : undefined} editable={isTextInputEditable} style={{textAlign:"center"}}/>
    <Text>Cognome</Text>
    <TextInput value={lastName} onChangeText={isEditing ? setLastName : undefined} editable={isTextInputEditable} style={{textAlign:"center"}}/>
    <Text>Data di nascita</Text>
    <TextInput value={dateOfBirth} onChangeText={isEditing ? setDateOfBirth : undefined} editable={isTextInputEditable} style={{textAlign:"center"}}/>
    <Text>Nazionalità</Text>
    <TextInput value={nationality} onChangeText={isEditing ? setNationality : undefined} editable={isTextInputEditable} style={{textAlign:"center"}}/>
    <Text>Città</Text>
    <TextInput value={city} onChangeText={isEditing ? setCity : undefined} editable={isTextInputEditable} style={{textAlign:"center"}}/>
    <Text>Telefono</Text>
    <TextInput value={phoneNumber} onChangeText={isEditing ? setPhoneNumber : undefined} editable={isTextInputEditable} style={{textAlign:"center"}}/>
    <Text>Email</Text>
    <TextInput value={email} onChangeText={isEditing ? setEmail : undefined}editable={isTextInputEditable} style={{textAlign:"center"}}/>
    <Text>Password</Text>
    <TextInput value={password} onChangeText={isEditing ? setPassword : undefined} secureTextEntry={true} editable={isTextInputEditable} style={{textAlign:"center"}}/>
    <Button title="Salva modifiche" onPress={handleSaveChanges} disabled={!isEditing} />
    <Button title="Modifica" onPress={handleEdit} disabled={isEditing} />
    <Button title="Logout" color="blue" onPress={() => dispatch(logout())} />
    </View>

    );
    };
