import React from "react";
import { View, Text, FlatList, StyleSheet } from "react-native";
import { CustomScreenFC } from "../models/ScreenFC";
import { useSelector } from "react-redux";
import Card from "../components/Card/Card";
import {
  BookmarkProps,
  removeAllBookmark,
  removeBookmark,
} from "../redux/actions/bookmarkActions";
import { TouchableOpacity } from "react-native-gesture-handler";
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { useDispatch } from "react-redux";


const Favorites: CustomScreenFC<"Favorite"> = () => {
  const { bookmarks } = useSelector(
    (state: { bookmarkReducer: BookmarkProps }) => state.bookmarkReducer
  );

  const dispatch = useDispatch();

  return (
    <View

    >
      {bookmarks.length > 0 ? (
        <View>
          <TouchableOpacity

            activeOpacity={0.4}
            onPress={() => dispatch(removeAllBookmark())}>
            <MaterialCommunityIcons name="delete" color="red" size={30} />
          </TouchableOpacity>

          <FlatList
  data={bookmarks}
  numColumns={3}
  showsVerticalScrollIndicator={true}
  renderItem={({ item, index }) => (
    <View>
      <Card
        item={item}
        index={index}
        key={index}
        disabled
        onPress={() => dispatch(removeBookmark(item.id))}
        isBookmarked={false}
        isFavorite={true}
      />
      <View>
        <MaterialCommunityIcons
          name="account"
          color="black"
          size={20}
        />
        <Text>
          {item.firstName} {item.lastName}
        </Text>
      </View>
      <View>
        <MaterialCommunityIcons
          name="phone"
          color="black"
          size={20}
        />
        <Text >{item.phone}</Text>
      </View>
    </View>
  )}
/>


          </View>
      ) : (
        <View>
          <Text>
            No Bookmark present
          </Text>
        </View>
      )}
    </View>
  );
};




export default Favorites;
