import { StatusBar } from "expo-status-bar";
import React, { useEffect, useRef, useState } from "react";
import {View,SafeAreaView,Button,FlatList,Text,StyleSheet, TouchableOpacity, Alert} from "react-native";
import Card from "../components/Card/Card";
import { Data } from "../models/Data";
import { ScreenFC } from "../models/ScreenFC";
import { useDispatch } from "react-redux";
import { ScrollView } from "react-native-gesture-handler";

const HomeScreen: ScreenFC<"Home"> = ({ navigation, route }) => {
  const [users, setUsers] = useState<Data[]>([]);
  const [bookmarkedIds, setBookmarkedIds] = useState<number[]>([]);
  const [isTouching, setIsTouching] = useState(false);
  const intervalRef = useRef<NodeJS.Timeout>();
  const dispatch = useDispatch();

  const fetchUsers = () => {
    fetch("https://randomuser.me/api/?results=26")
      .then((response) => response.json())
      .then((data) => {
        const usersData: Data[] = data.results.map((user: any) => ({
          firstName: user.name.first,
          lastName: user.name.last,
          phone: user.phone,
          picture: user.picture.large,
          id: user.id.value,
        }));
        setUsers(usersData);
        dispatch(updateUsers(usersData));
      })
      .catch((error) => console.log(error));
  };

  const handleBookmarkChange = (id: number, isBookmarked: boolean) => {
    if (isBookmarked) {
      setBookmarkedIds([...bookmarkedIds, id]);
    } else {
      setBookmarkedIds(bookmarkedIds.filter((i) => i !== id));
    }
    setIsTouching(true);
  };

  const handleCardPress = (id: number) => {
    setIsTouching(true);
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  useEffect(() => {
    intervalRef.current = setInterval(() => {
      if (!isTouching) {
        Alert.alert("Updating Users", "Fetching new users data", [
          { text: "OK", onPress: fetchUsers },
        ]);
      } else {
        setIsTouching(false);
      }
    }, 2000);

    return () => {
      clearInterval(intervalRef.current);
    };
  }, [isTouching]);

  return (
    <View style={styles.container}>
      <ScrollView>
        <StatusBar style="auto" />
        <View style={styles.buttonContainer}></View>
        <View style={styles.shadow}>
          {users.length > 0 ? (
            <FlatList
              style={{ marginBottom: 120 }}
              data={users}
              numColumns={3}
              showsVerticalScrollIndicator={false}
              renderItem={({ item }) => (
                <TouchableOpacity
                  onPress={() => handleCardPress(item.id)}
                  activeOpacity={0.8}
                >
                  <Card
                    item={item}
                    isBookmarked={bookmarkedIds.includes(item.id)}
                    index={0}
                    onBookmarkChange={(id, isBookmarked) =>
                      handleBookmarkChange(id, isBookmarked)
                    }
                  />
                </TouchableOpacity>
              )}
            />
          ) : (
            <View style={styles.cardContainer}>
              <Text>No result</Text>
            </View>
          )}
        </View>
      </ScrollView>
    </View>
  );
};


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#ff56",
    paddingHorizontal: 0,
    alignItems: "center",
    justifyContent:"space-between"

  },
  buttonContainer: {
    flexDirection: "row",
    alignSelf: "center",
    padding: 10,
  },
  shadow: {
    elevation: 2,
    shadowColor: "black",
    shadowOffset: {
      height: 20,
      width: 15,
    },
    shadowOpacity: 0.5,
    shadowRadius: 7,
  },
  cardContainer: {
    backgroundColor: "#fff",
    height: 170,
    width: "100%",
    borderRadius: 30,
    alignItems: "center",
    justifyContent: "center",
  },
});

export default HomeScreen;
