import React, { useState } from 'react';
import { TouchableOpacity, View, Image, StyleSheet } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { useDispatch } from 'react-redux';
import { addBookmark, removeBookmark } from '../../redux/actions/bookmarkActions';
import { Data } from '../../models/Data';
import { MaterialCommunityIcons } from '@expo/vector-icons';

interface Props {
  item: Data;
  isBookmarked: boolean;
  index: number;
  disabled?: boolean;
  onPress?: () => void;
  onBookmarkChange?: (id: number, isBookmarked: boolean) => void;
  isFavorite?: boolean;  // aggiunta della prop isFavorite
}


const Card = ({ item, isBookmarked, isFavorite = false }: Props) => {
  const dispatch = useDispatch();
  const [bookmarked, setBookmarked] = useState(isBookmarked);

  const handleCardAction = (action: "bookmark" | "remove") => {
    setBookmarked(!bookmarked);
    if (action === "bookmark") {
      dispatch(addBookmark(item));
    } else {
      dispatch(removeBookmark(item.id));
    }
  };

  const icon = isFavorite ? (
    <MaterialCommunityIcons
      name="delete"
      size={20}
      color="red"
      onPress={() => handleCardAction("remove")}
    />
  ) : (
    <Icon
      name="heart"
      size={20}
      color={bookmarked ? "red" : "white"}
      onPress={() => handleCardAction("bookmark")}
    />
  );

  return (
    <View style={styles.cardContainer}>
      <TouchableOpacity activeOpacity={0.5}>
        <Image source={{ uri: item.picture }} style={styles.image} />
      </TouchableOpacity>
      <View style={styles.iconContainer}>{icon}</View>
    </View>
  );
};

const styles = StyleSheet.create({
  cardContainer: {
    backgroundColor: "none",
    height: 150,
    width: 100,
    padding: 10,
    borderRadius: 0,
    alignItems: "center",
    marginVertical: 15,
    margin: 10,
    justifyContent: "flex-start",
  },
  image: {
    height: 100,
    width: 100,
    borderRadius: 0,
  },
  iconContainer: {
    position: "absolute",
    right: 0,
    bottom: 0,
  },

});

export default Card;
