import { StatusBar } from "expo-status-bar";
import React, { useEffect, useState } from "react";
import {
  View,
  SafeAreaView,
  Button,
  FlatList,
  Text,
  StyleSheet,
} from "react-native";
import Card from "../components/Card/Card";
import { Data } from "../models/Data";
import { ScreenFC } from "../models/ScreenFC";
import { useDispatch } from "react-redux";
import { addBookmark } from "../redux/actions/bookmarkActions";
import { Directions, ScrollView } from "react-native-gesture-handler";

const HomeScreen: ScreenFC<"Home"> = ({ navigation, route }) => {
  const [stateapi, setState] = useState<Array<Data>>([]);
  const dispatch = useDispatch();

  interface UserData {
    firstName: string;
    lastName: string;
    phone: string;
    picture: string;
  }

  useEffect(() => {
    fetch('https://randomuser.me/api/?results=26')
      .then(response => response.json())
      .then(data => {
        const users: UserData[] = data.results.map((user: any) => ({
          firstName: user.name.first,
          lastName: user.name.last,
          phone: user.phone,
          picture: user.picture.large,
        }));
        setState(users);
      })
      .catch(error => console.log(error));
  }, []);

  useEffect(() => {
    const timeout = setTimeout(() => {
      fetch('https://randomuser.me/api/?results=26')
        .then(response => response.json())
        .then(data => {
          const users: UserData[] = data.results.map((user: any) => ({
            firstName: user.name.first,
            lastName: user.name.last,
            phone: user.phone,
            picture: user.picture.large,
          }));
          setState(users);
        })
        .catch(error => console.log(error));
    }, 12000); // 120 seconds
    return () => clearTimeout(timeout);
  }, [stateapi]);

  return (
    <View style={styles.container}>
      <ScrollView>
        <StatusBar style="auto" />
        <View style={styles.buttonContainer}>
        </View>
        <View style={styles.shadow}>
          {stateapi.length > 0 ? (
            <FlatList
              style={{ marginBottom: 120 }}
              data={stateapi}
              showsVerticalScrollIndicator={false}
              numColumns={4}
              renderItem={({ item}) => (
                <Card
                  item={item}
                />
              )}
            />
          ) : (
            <View style={styles.cardContainer}>
              <Text>No result</Text>
            </View>
          )}
        </View>
      </ScrollView>
    </View>
  );
};


const styles = StyleSheet.create({
  container: {
    display: "flex",
    flexDirection: "row",
    backgroundColor: "rgb(79,172,217)",
    paddingHorizontal: 10,
  },
  buttonContainer: {
    flexDirection: "row",
    alignSelf: "center",
    padding: 10,
  },
  shadow: {
    elevation: 2,
    shadowColor: "black",
    shadowOffset: {
      height: 20,
      width: 15,
    },
    shadowOpacity: 0.5,
    shadowRadius: 7,
  },
  cardContainer: {
    backgroundColor: "#fff",
    height: 150,
    width: 120,
    padding: 15,
    borderRadius: 0,
    alignItems: "center",
    marginVertical: 10,
    margin: 30,
    justifyContent: "flex-start",
  },
});

export default HomeScreen;
