import React, { useState } from 'react';
import { TouchableOpacity, View, Image, StyleSheet, Modal } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { useDispatch } from 'react-redux';
import { addBookmark, removeBookmark } from '../../redux/actions/bookmarkActions';
import { Data } from '../../models/Data';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { Text } from 'react-native';


interface Props {
  item: Data;
  isBookmarked: boolean;
  index: number;
  disabled?: boolean;
  onPress?: () => void;
  onBookmarkChange?: (id: number, isBookmarked: boolean) => void;
  isFavorite?: boolean;  // aggiunta della prop isFavorite
}


const Card = ({ item, isBookmarked, isFavorite = false }: Props) => {
  const dispatch = useDispatch();
  const [bookmarked, setBookmarked] = useState(isBookmarked);
  const [modalVisible, setModalVisible] = useState(false);


  const handleCardAction = (action: "bookmark" | "remove") => {
    setBookmarked(!bookmarked);
    if (action === "bookmark") {
      dispatch(addBookmark(item));
    } else {
      dispatch(removeBookmark(item.id));
    }
  };

  const icon = isFavorite ? (
    <MaterialCommunityIcons
      name="delete"
      size={20}
      color="red"
      onPress={() => handleCardAction("remove")}
    />
  ) : (
    <Icon
      name="heart"
      size={20}
      color={bookmarked ? "red" : "white"}
      onPress={() => handleCardAction("bookmark")}
    />
  );



  return (
    <><View style={styles.cardContainer}>
      <TouchableOpacity activeOpacity={0.5} onPress={() => setModalVisible(true)}>
        <Image source={{ uri: item.picture }} style={styles.image} />
      </TouchableOpacity>
      <View style={styles.iconContainer}>{icon}</View>
    </View>

    <Modal visible={modalVisible} animationType="slide">
        <View style={styles.modalContainer}>
          <TouchableOpacity onPress={() => setModalVisible(false)}>
            <Icon name="close" size={20} color="white" />
          </TouchableOpacity>
          <Image source={{ uri: item.picture }} style={styles.modalImage} />
          <Text style={styles.modalText}> <MaterialCommunityIcons name="face-man-profile" size={20} color="white"
    />{item.lastName} {item.firstName}</Text>

          <Text style={styles.modalText}><MaterialCommunityIcons name="phone" size={20} color="white"
    />{item.phone}</Text>
          <Text style={styles.modalText}><MaterialCommunityIcons name="email" size={20} color="white"
    />{item.email}</Text>
        </View>
      </Modal></>


  );


};

const styles = StyleSheet.create({
  cardContainer: {
    backgroundColor: "none",
    height: 150,
    width: 100,
    padding: 10,
    borderRadius: 0,
    alignItems: "center",
    marginVertical: 15,
    margin: 10,
    justifyContent: "flex-start",
  },
  image: {
    height: 100,
    width: 100,
    borderRadius: 0,
  },
  iconContainer: {
    position: "absolute",
    right: 80,
    bottom: 12,
  },

  modalContainer: {
    backgroundColor: "black",
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  modalImage: {
    height: 200,
    width: 200,
    borderRadius: 100,
    marginVertical: 20,
  },
  modalText: {
    color: "white",
    fontSize: 16,
    marginVertical: 5,
  },


});

export default Card;
