import React, { useState } from "react";
import { Button, Text, TextInput, View } from "react-native";
import { ScreenFC } from "../models/ScreenFC";
import { useDispatch } from "react-redux";
import { signUp } from "../redux/actions/accountActions";
import * as ImagePicker from 'expo-image-picker';

interface CustomImagePickerResult {
  cancelled: boolean;
  uri: string;
  type?: string;
  width?: number;
  height?: number;
}


const SignUp: ScreenFC<"SignUp"> = ({ navigation }) => {
  const [email, setEmail] = useState<string>("");
  const [password, setPassword] = useState<string>("");
  const [imageUri, setImageUri] = useState<string>("");
  const [firstName, setFirstName] = useState<string>("");
  const [lastName, setLastName] = useState<string>("");
  const [dateOfBirth, setDateOfBirth] = useState<string>("");
  const [nationality, setNationality] = useState<string>("");
  const [city, setCity] = useState<string>("");
  const [phoneNumber, setPhoneNumber] = useState<string>("");

  const dispatch = useDispatch();

  const handleImagePicker = async () => {
    const { status } =
      await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (status !== "granted") {
      alert("Per favore, abilita i permessi per la libreria multimediale.");
      return;
    }
    const result = (await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      quality: 1,
    })) as unknown as CustomImagePickerResult;
    if (!result.cancelled) {
      setImageUri(result.uri);
    }
  };

  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
      <Text>Registrati</Text>
      <TextInput
        placeholder="Nome"
        onChangeText={(value) => setFirstName(value)}
      />
      <TextInput
        placeholder="Cognome"
        onChangeText={(value) => setLastName(value)}
      />
      <TextInput
        placeholder="Data di nascita"
        onChangeText={(value) => setDateOfBirth(value)}
      />
      <TextInput
        placeholder="Nazionalità"
        onChangeText={(value) => setNationality(value)}
      />
      <TextInput
        placeholder="Città"
        onChangeText={(value) => setCity(value)}
      />
      <TextInput
        placeholder="Telefono"
        onChangeText={(value) => setPhoneNumber(value)}
      />
      <TextInput
        placeholder="Email"
        onChangeText={(value) => setEmail(value)}
      />
      <TextInput
        placeholder="Password"
        secureTextEntry={true}
        onChangeText={(value) => setPassword(value)}
      />
     <Button title="Cambia immagine" onPress={handleImagePicker} />
      <Button
        title="Registrati"
        color="red"
        onPress={() => {
          email &&
            password &&
            dispatch(
              signUp({
                email,
                password,
                isLogged: true,
                firstName,
                lastName,
                dateOfBirth,
                nationality,
                city,
                phoneNumber,
                image: imageUri,
              })
            );
        }}
      />
      <View style={{ flexDirection: "row", alignItems: "center" }}>
        <Text>Hai un account?</Text>
        <Button title="Accedi" onPress={() => navigation.navigate("Login")} />
      </View>
    </View>
  );
};

export default SignUp;
