import React, { useState } from "react";
import { Image, Text, TextInput, View, StyleSheet, Pressable } from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { AccountAction, ACCOUNT_ACTIONS, AccountProps} from "../redux/actions/accountActions";
import * as ImagePicker from 'expo-image-picker';
import { CustomScreenFC} from "../models/ScreenFC";
import { ScrollView } from "react-native-gesture-handler";


export const Personal = () => {
 


  return (
    <ScrollView>
    <View style={styles.container}>
        <Image
          source={{ uri: imageUri }}
          style={styles.profileImage}
          resizeMode="cover"/>

        <Pressable onPress={handleImagePicker} style={[styles.buttonimg]}><Text style={styles.buttonTextimg}>Cambia immagine</Text></Pressable>
        <Text style={styles.label}>Nome</Text>
        <TextInput
          value={firstName}
          onChangeText={isEditing ? setFirstName : undefined}
          editable={isTextInputEditable}
          style={styles.input}
        />
        <Text style={styles.label}>Cognome</Text>
        <TextInput
          value={lastName}
          onChangeText={isEditing ? setLastName : undefined}
          editable={isTextInputEditable}
          style={styles.input}
        />
        <Text style={styles.label}>Data di nascita</Text>
        <TextInput 
          value={dateOfBirth} 
          onChangeText={isEditing ? setDateOfBirth : undefined} 
          editable={isTextInputEditable} 
          style={styles.input}/>
        <Text style={styles.label}>Nazionalità</Text>
        <TextInput 
          value={nationality} 
          onChangeText={isEditing ? setNationality : undefined} 
          editable={isTextInputEditable} 
          style={styles.input}/>
        <Text style={styles.label}>Città</Text>
        <TextInput 
          value={city} 
          onChangeText={isEditing ? setCity : undefined} 
          editable={isTextInputEditable} 
          style={styles.input}/>
        <Text style={styles.label}>Telefono</Text>
        <TextInput 
          value={phoneNumber} 
          onChangeText={isEditing ? setPhoneNumber : undefined} 
          editable={isTextInputEditable} 
          style={styles.input}/>
        <Text style={styles.label}>Email</Text>
        <TextInput 
          value={email} 
          onChangeText={isEditing ? setEmail : undefined}
          editable={isTextInputEditable} 
          style={styles.input}/>
        <Text style={styles.label}>Password</Text>
        <TextInput 
          value={password} 
          onChangeText={isEditing ? setPassword : undefined} 
          secureTextEntry={true} editable={isTextInputEditable} 
          style={styles.input}/>
          
        <Pressable onPress={handleEdit} disabled={isEditing} 
        style={[styles.button]}><Text style={styles.buttonText}>Edit Profile</Text> </Pressable>

        <Pressable onPress={handleSaveChanges} disabled={!isEditing}  
        style={[styles.button]}><Text style={styles.buttonText}>Save Changes</Text></Pressable>
        </View>
        </ScrollView>
  )
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      alignItems: "center",
      justifyContent: "center",
      paddingHorizontal: 10,
    },
    profileImage: {
      maxWidth: 120,
      maxHeight: 120,
      width: "100%",
      height: "100%",
      borderRadius: 100,
    },
    input: {
      textAlign: "center",
      marginBottom: 5,
      fontSize: 12,
    },
    label: {
      fontWeight: "bold",
      fontSize: 14,
      marginTop: 5,
      marginBottom: 5,
    },
    button: {
      marginTop: 20,
      backgroundColor: "#4CAF50",
      borderRadius: 5,
      padding: 10,
      minWidth: 150,
      alignItems: "center",
      justifyContent: "center",
      height: 30,

    },
    buttonimg: {
      height: 30,
      marginTop: 20,
      marginBottom:10,
      borderRadius: 5,
      padding: 10,
      minWidth: 150,
      alignItems: "center",
      justifyContent: "center",

    },
    buttonTextimg: {
      fontWeight: "bold",
      fontSize: 14,
      marginTop: 5,
      marginBottom: 5,
    },
    buttonText: {
      color: "#fff",
      fontWeight: "bold",
      fontSize: 12,
    },
  });

export default Personal;
