import { StatusBar } from "expo-status-bar";
import React, { useEffect, useState, useRef } from "react";
import {
  View,
  SafeAreaView,
  Button,
  FlatList,
  Text,
  StyleSheet,
} from "react-native";
import Card from "../components/Card/Card";
import { Data } from "../models/Data";
import { ScreenFC } from "../models/ScreenFC";
import { useDispatch } from "react-redux";
import { addBookmark } from "../redux/actions/bookmarkActions";
import { ScrollView } from "react-native-gesture-handler";

const HomeScreen: ScreenFC<"Home"> = ({ navigation, route }) => {
  const [state, setState] = useState<Array<Data>>([]);
  const [index, setIndex] = useState<number>(0);
  const timerRef = useRef<NodeJS.Timeout>();

  const dispatch = useDispatch();

  interface UserData {
    firstName: string;
    lastName: string;
    phone: string;
    picture: string;
  }

  useEffect(() => {
    fetch('https://randomuser.me/api/?results=26')
      .then(response => response.json())
      .then(data => {
        const users: UserData[] = data.results.map((user: any) => ({
          firstName: user.name.first,
          lastName: user.name.last,
          phone: user.phone,
          picture: user.picture.large,
        }));
        setState(users);
      })
      .catch(error => console.log(error));
  }, []);

  useEffect(() => {
    timerRef.current = setTimeout(() => {
      setIndex((index + 1) % state.length);
    }, 120000);
    return () => {
      clearTimeout(timerRef.current);
    };
  }, [index, state]);

  return (
    <View style={styles.container}>
      <ScrollView>
        <StatusBar style="auto" />
        <View style={styles.buttonContainer}></View>

        <View style={styles.shadow}>
          {state.length > 0 ? (
            <FlatList
              style={{ marginBottom: 120 }}
              data={[state[index]]}
              showsVerticalScrollIndicator={false}
              numColumns={3}
              renderItem={({ item }) => <Card item={item} />}
            />
          ) : (
            <View style={styles.cardContainer}>
              <Text>No result</Text>
            </View>
          )}
        </View>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "rgb(79,172,217)",
    paddingHorizontal: 20,
  },
  buttonContainer: {
    flexDirection: "row",
    alignSelf: "center",
    padding: 10,
  },
  shadow: {
    elevation: 2,
    shadowColor: "black",
    shadowOffset: {
      height: 20,
      width: 15,
    },
    shadowOpacity: 0.5,
    shadowRadius: 7,
  },
  cardContainer: {
    backgroundColor: "#fff",
    height: 170,
    width: "100%",
    borderRadius: 30,
    alignItems: "center",
    justifyContent: "center",
  },
});

export default HomeScreen;
