import React from "react";
import { View, Text, ScrollView,StyleSheet } from "react-native";
import { CustomScreenFC } from "../models/ScreenFC";
import { useSelector } from "react-redux";
import { Data } from "../models/Data";
import Card from "../components/Card/Card";
import {
  BookmarkProps,
  removeAllBookmark,
  removeBookmark,
} from "../redux/actions/bookmarkActions";
import { TouchableOpacity } from "react-native-gesture-handler";
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { useDispatch } from "react-redux";


const Favorites: CustomScreenFC<"Favorite"> = () => {
  const { bookmarks } = useSelector(
    (state: { bookmarkReducer: BookmarkProps }) => state.bookmarkReducer
  );

  const dispatch = useDispatch();

  return (
    <View
      style={{
        backgroundColor: "#fc8386",
        paddingHorizontal: 20,
        flex: 1,
      }}
    >
      {bookmarks.length > 0 ? (
        <View>
          <TouchableOpacity
            style={{ paddingVertical: 20, alignItems: "flex-end" }}
            activeOpacity={0.4}
            onPress={() => dispatch(removeAllBookmark())}>
            <MaterialCommunityIcons name="delete" color="red" size={30} />
          </TouchableOpacity>
          <View  style={{
              width: "100%",
              height:"auto",
              display:"flex",
              flexDirection:"row",
              flexWrap:"wrap"}}>
         <ScrollView
  showsVerticalScrollIndicator={false}
  style={{
    width: "90%",
    elevation: 2,
    shadowColor: "black"
  }} 
>
            {bookmarks?.map((item: Data, index: number) => {
              return (
                <Card
                item={item}
                index={index}
                key={index}
                disabled
                onPress={() => dispatch(removeBookmark(item.id))}
                isBookmarked={false}
                isFavorite={true}  // aggiunta della prop isFavorite
              />
              );
            })}
          </ScrollView>
          </View>
        </View>
      ) : (
        <View>
          <Text style={{ color: "#000000", fontSize: 20 }}>
            No Bookmark present
          </Text>
        </View>
      )}
    </View>
  );
};




export default Favorites;
