import React, { useState } from 'react';
import { TouchableOpacity, View, Image, StyleSheet } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { useDispatch } from 'react-redux';
import { addBookmark, removeBookmark } from '../../redux/actions/bookmarkActions';
import { Data } from '../../models/Data';

interface Props {
  item: Data;
  isBookmarked: boolean;
  index: number;
  disabled?: boolean;
  onPress?: () => void;
  onBookmarkChange?: (id: number, isBookmarked: boolean) => void;
}


const Card = ({ item, isBookmarked }: Props) => {
  const dispatch = useDispatch();
  const [bookmarked, setBookmarked] = useState(isBookmarked);



  const handleBookmarkPress = () => {
    setBookmarked(!bookmarked);
    if (bookmarked) {
      dispatch(removeBookmark(item.id));
    } else {
      dispatch(addBookmark(item));
    }
  };
  const selectedIconStyle = {
    color: 'red',
  };
  const unselectedIconStyle = {
    color: 'white',
  };
  const iconStyle = bookmarked ? selectedIconStyle : unselectedIconStyle;

  return (

      <View style={styles.cardContainer}>
        <TouchableOpacity activeOpacity={0.5}>
        <Image source={{ uri: item.picture }} style={styles.image} />
        </TouchableOpacity>
        <View style={styles.iconContainer}>
        </View>

        <Icon
            name="heart"
            size={30}
            style={iconStyle}
            onPress={handleBookmarkPress}
          />
      </View>

  );
};

const styles = StyleSheet.create({
  cardContainer: {
    backgroundColor: "none",
    height: 150,
    width: 100,
    padding: 10,
    borderRadius: 0,
    alignItems: "center",
    marginVertical: 15,
    margin: 10,
    justifyContent: "flex-start",
  },
  image: {
    height: 100,
    width: 100,
    borderRadius: 0,
  },
  iconContainer: {
    position: "absolute",
    right: 0,
    bottom: 20,
  },

});

export default Card;
