import React, { useState } from "react";
import { Button, Image, Text, TextInput, View } from "react-native";
import { ScreenFC } from "../models/ScreenFC";
import { useDispatch, useSelector } from "react-redux";
import { Account, AccountAction, ACCOUNT_ACTIONS, AccountProps } from "../redux/actions/accountActions";
import * as ImagePicker from 'expo-image-picker';

const ProfileScreen: ScreenFC<"Profile"> = () => {
  const { account } = useSelector(
    (state: { accountReducer: AccountProps }) => state.accountReducer
  );

const dispatch = useDispatch();

const [email, setEmail] = useState<string>(account.email);
const [password, setPassword] = useState<string>(account.password);
const [imageUri, setImageUri] = useState<string>(account.image);
const [firstName, setFirstName] = useState<string>(account.firstName);
const [lastName, setLastName] = useState<string>(account.lastName);
const [dateOfBirth, setDateOfBirth] = useState<string>(account.dateOfBirth);
const [nationality, setNationality] = useState<string>(account.nationality);
const [city, setCity] = useState<string>(account.city);
const [phoneNumber, setPhoneNumber] = useState<string>(account.phoneNumber);
const [isEditing, setIsEditing] = useState<boolean>(false);


interface CustomImagePickerResult {
  cancelled: boolean;
  uri: string;
  type?: string;
  width?: number;
  height?: number;
}

const handleSaveChanges = () => {
dispatch({
type: ACCOUNT_ACTIONS.ADD_ACCOUNT,
payload: {
email,
password,
isLogged: true,
firstName,
lastName,
dateOfBirth,
nationality,
city,
phoneNumber,
image: imageUri,
},
} as AccountAction);
};

const handleImagePicker = async () => {
const { status } =
await ImagePicker.requestMediaLibraryPermissionsAsync();
if (status !== "granted") {
alert("Per favore, abilita i permessi per la libreria multimediale.");
return;
}
const result = await ImagePicker.launchImageLibraryAsync({
  mediaTypes: ImagePicker.MediaTypeOptions.Images,
  allowsEditing: true,
  quality: 1,
}) as unknown as CustomImagePickerResult;
if (!result.cancelled) {
setImageUri(result.uri);
}
};

return (
<View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
<Image source={{ uri: imageUri }} style={{ width: 200, height: 200, borderRadius: 100 }} />
<Button title="Cambia immagine" onPress={handleImagePicker} />
<Text>Nome</Text>
<TextInput value={firstName} onChangeText={setFirstName} />
<Text>Cognome</Text>
<TextInput value={lastName} onChangeText={setLastName} />
<Text>Data di nascita</Text>
<TextInput value={dateOfBirth} onChangeText={setDateOfBirth} />
<Text>Nazionalità</Text>
<TextInput value={nationality} onChangeText={setNationality} />
<Text>Città</Text>
<TextInput value={city} onChangeText={setCity} />
<Text>Telefono</Text>
<TextInput value={phoneNumber} onChangeText={setPhoneNumber} />
<Text>Email</Text>
<TextInput value={email} onChangeText={setEmail} />
<Text>Password</Text>
<TextInput value={password} onChangeText={setPassword} secureTextEntry={true} />
<Button title="Salva modifiche" onPress={handleSaveChanges} />
</View>
);
};

export default ProfileScreen;
